import { Component } from '@angular/core';
import * as _ from 'lodash';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {

    Toppings = [{
      "name" : "Tomatoes",
      "category" : "Veg",
      "ammount" : 1.00,
    },{
      "name" : "Onions",
      "category" : "Veg",
      "ammount" : .50,
    },
    {
      "name" : "Bell pepper",
      "category" : "Veg",
      "ammount" : 1.00,
    },
    {
      "name" : "Mushrooms",
      "category" : "Veg",
      "ammount" : 1.20,
    },
    {
      "name" : "Pineapple",
      "category" : "Non Veg",
      "ammount" : .75,
    },
    {
      "name" : "Sausage",
      "category" : "Non Veg",
      "ammount" : 1.00,
    },
    {
      "name" : "Pepperoni",
      "category" : "Non Veg",
      "ammount" : 2.00,
    },
    {
      "name" : "Chicken",
      "category" : "Non Veg",
      "ammount" : 3.00,
    }]

    Sizes = [{
      "Name" : "Small",
      "Amount" : 5.00
    },
    {
      "Name" : "Medium",
      "Amount" : 7.00
    },
    {
      "Name" : "Large",
      "Amount" : 8.00
    },
    {
      "Name" : "Extra Large",
      "Amount" : 9.00
    }]

    gorups :any;
    constructor(){

      this.gorups = _.groupBy(this.Toppings,"category");
      console.log(this.gorups)
    }

}
