import { Component } from '@angular/core';
import * as _ from 'lodash';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {

  Toppings = [{
    "name": "Tomatoes",
    "category": "Veg",
    "amount": 1.00,
  }, {
    "name": "Onions",
    "category": "Veg",
    "amount": .50,
  },
  {
    "name": "Bell pepper",
    "category": "Veg",
    "amount": 1.00,
  },
  {
    "name": "Mushrooms",
    "category": "Veg",
    "amount": 1.20,
  },
  {
    "name": "Pineapple",
    "category": "Veg",
    "amount": .75,
  },
  {
    "name": "Sausage",
    "category": "Non Veg",
    "amount": 1.00,
  },
  {
    "name": "Pepperoni",
    "category": "Non Veg",
    "amount": 2.00,
  },
  {
    "name": "Chicken",
    "category": "Non Veg",
    "amount": 3.00,
  }]

  Sizes = [{
    "Name": "Small",
    "Amount": 5.00
  },
  {
    "Name": "Medium",
    "Amount": 7.00
  },
  {
    "Name": "Large",
    "Amount": 8.00
  },
  {
    "Name": "Extra Large",
    "Amount": 9.00
  }]

  carts: any[] = []
  gorups: any;
  constructor() {

    this.gorups = _.groupBy(this.Toppings.reverse(), "category");
    console.log(this.gorups)
  }

  getToppings(name: any) {

    return _.filter(this.Toppings, { category: name })
  }
  addCart(s: any) {

    var _dccc = document.querySelectorAll(`[data-size=${s.Name}]`)
    var _toppings: any[] = [];
    _dccc.forEach(e => {
      if (e instanceof HTMLInputElement) {
        if (e.checked) {
          _toppings.push(e.dataset.topping)
        }
      }
    })
    var _items: any = {
      Toppings: _toppings,
      Qty: 1,
      Size: s.Name
    }
    _items.Amount = 0;
    _items.Amount = _items.Amount + _.chain(this.Toppings).filter(e => {
      return _toppings.indexOf(e.name) !== -1
    }).sumBy('amount').value();

    _items.Amount = _items.Amount + s.Amount;

    this.carts.push(_items);

    this.calcTotal();
  }
  total = 0;
  calcTotal(){
    
    this.total = _.chain(this.carts).sumBy('Amount').value()
  }

  removeItem(index: number) {
    this.carts.splice(index, 1);
    this.calcTotal();
  }
}
