import { Component } from '@angular/core';
import * as _ from 'lodash';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {

  Toppings = [{
    "name": "Tomatoes",
    "category": "Veg",
    "amount": 1.00,
  }, {
    "name": "Onions",
    "category": "Veg",
    "amount": .50,
  },
  {
    "name": "Bell pepper",
    "category": "Veg",
    "amount": 1.00,
  },
  {
    "name": "Mushrooms",
    "category": "Veg",
    "amount": 1.20,
  },
  {
    "name": "Pineapple",
    "category": "Veg",
    "amount": .75,
  },
  {
    "name": "Sausage",
    "category": "Non Veg",
    "amount": 1.00,
  },
  {
    "name": "Pepperoni",
    "category": "Non Veg",
    "amount": 2.00,
  },
  {
    "name": "Chicken",
    "category": "Non Veg",
    "amount": 3.00,
  }]

  Sizes = [{
    "Name": "Small",
    "Amount": 5.00
  },
  {
    "Name": "Medium",
    "Amount": 7.00
  },
  {
    "Name": "Large",
    "Amount": 8.00
  },
  {
    "Name": "Extra Large",
    "Amount": 9.00
  }]
  offers = [{
    "type" : 'fixed',
    "value" : 5,
    "criteria" : {
      toppingQty : 2,
      size : 'Medium',
      Qty : 1
      
    },
    "name" : "Offer 1"
  },
  {
    "type" : 'fixed',
    "value" : 9,
    "criteria" : {
      toppingQty : 4,
      size : 'Medium',
      Qty : 2,
      
    },
    "name" : "Offer 1"
  },
  {
    "type" : 'percent',
    "value" : 50,
    "criteria" : {
      toppingQty : 4,
      size : 'Extra Large',
      Qty : 1,
      
    },
    "name" : "Offer 1"
  }]
  carts: any[] = []
  gorups: any;
  constructor() {

    this.gorups = _.groupBy(this.Toppings.reverse(), "category");
    console.log(this.gorups)
  }

  getToppings(name: any) {

    return _.filter(this.Toppings, { category: name })
  }
  addCart(s: any) {

    var _dccc = document.querySelectorAll(`[data-size='${s.Name}']`)
    var _toppings: any[] = [];
    _dccc.forEach(e => {
      if (e instanceof HTMLInputElement) {
        if (e.checked) {
          _toppings.push(e.dataset.topping)
        }
      }
    })

    if(_toppings.length == 0)
    {
      alert("At-least one topping requried")
      return;
    }
    var _items: any = {
      Toppings: _toppings,
      Qty: 1,
      Size: s.Name,
      Amount : 0 
    }
    _items.Amount = s.Amount + _.chain(this.Toppings)
    .filter(e => _toppings.indexOf(e.name) !== -1)
    .sumBy('amount')
    .value();

    this.carts.push(_items);
    this.calcTotal();
    this.clear(s);
    this.applyOffers();
  }

  totalDiscount = 0;
  basePrice = 0;
  applyOffered:any[] = [];
  removeOffers (){

  }
  applyOffers() {
    let totalDiscount = 0; // Initialize total discount
    this.applyOffered = [];
    this.carts.forEach(item => {
      this.offers.forEach(offer => {
        const criteria = offer.criteria;
        if (
          item.Toppings.length >= criteria.toppingQty &&
          item.Size === criteria.size &&
          item.Qty >= criteria.Qty
        ) {
          this.applyOffered.push(offer)
          if (offer.type === 'fixed') {
            totalDiscount += offer.value;
          } else if (offer.type === 'percent') {
            const discount = (offer.value / 100) * item.Amount;
            totalDiscount += discount;
          }
        }
      });
    });
    this.totalDiscount = totalDiscount;
    this.total = this.basePrice - totalDiscount; // Apply the total discount
  }
  
  clear(s:any){
    var _dccc = document.querySelectorAll(`[data-size='${s.Name}']`)
    _dccc.forEach(e => {
      if (e instanceof HTMLInputElement) {
        e.checked = false;
      }
    })
  }
  total = 0;
  calcTotal(){
    this.basePrice = Number(_.sumBy(this.carts, 'Amount').toFixed(2));
    this.total = this.basePrice - this.totalDiscount 
  }

  removeItem(index: number) {
    this.carts.splice(index, 1);
    this.calcTotal();
    this.applyOffers();
  }

  
}


<div class="row">
  <div class="col-8">
    <h3>Order</h3>
    <table class="table table-bordered">
      <thead class="">
        <tr>
          <th rowspan="2" class="text-center align-middle bg-light" style="vertical-align: middle;">
            Toppings
          </th>
          <th [attr.colspan]="Sizes.length" class="text-center bg-light">
            Pizza size
          </th>
        </tr>
        <tr>
          <th *ngFor="let s of Sizes" class="text-center bg-light">
            {{ s.Name }} (${{ s.Amount }})
          </th>
        </tr>
      </thead>
      <tbody>
        <ng-container *ngFor="let s of gorups | keyvalue">
          <tr>
            <th class="bg-light" [attr.colspan]="1 + Sizes.length">
              {{ s.key }}
            </th>
          </tr>
          <tr *ngFor="let v of getToppings(s.key)">
            <th class="text-center">{{ v.name }} (${{ v.amount }})</th>
            <th *ngFor="let s of Sizes" class="text-center">
              <div class="form-check d-inline-block">
                <input
                  class="form-check-input"
                  [attr.data-topping]="v.name"
                  [attr.data-size]="s.Name"
                  type="checkbox"
                  id="{{
                    v.name.replace(' ', '-') + '-' + s.Name.replace(' ', '-')
                  }}"
                />
                <label
                  class="form-check-label"
                  for="{{
                    v.name.replace(' ', '-') + '-' + s.Name.replace(' ', '-')
                  }}"
                >
                </label>
              </div>
            </th>
          </tr>
        </ng-container>
        <tr>
          <td></td>
          <th *ngFor="let s of Sizes" class="text-center">
            <button class="btn btn-primary" (click)="addCart(s)">Add Cart</button>
          </th>
        </tr>
      </tbody>
    </table>
  </div>
  
  <div class="col-4">
    <h3>Cart</h3>
    <table class="table table-bordered" *ngIf="carts.length > 0">
      <thead class="">
        <tr>
          <th class="bg-light">Description</th>
          <th class="bg-light" style="width: 50px">Qty</th>
          <th class="bg-light " style="width: 100px"> Price</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let c of carts; let i = index"  style="font-size: 14px;">
          
          <td>
            <p class="mb-0">Size : {{ c.Size }}</p>
            <p class="mb-0">Toppings : {{ c.Toppings.join(", ") }}</p>
            <a
              class="text-danger"
              href="javascript:void(0)"
              (click)="removeItem(i)"
            >
              remove
            </a>
          </td>
          <td>
            {{ c.Qty }}
          </td>
          <td>
            {{ c.Amount }}
          </td>
        </tr>
        
        <tr>
          <td colspan="2">
            <p>
              Offer
            </p>
            <p>
              <span class="badge bg-light text-dark" *ngFor="let offer of applyOffered">
                {{offer.name}} - {{offer.criteria.Qty}} {{offer.criteria.size}} / {{offer.criteria.toppingSize}} toppings
              </span>
            </p>

          </td>
          <td class="">
            <button class="btn btn-sm btn-primary" *ngIf="!applyOffered || applyOffered.length == 0" (click)="applyOffers()">Apply Offer</button>
            <button class="btn btn-sm btn-primary" *ngIf="applyOffered.length >= 0" (click)="removeOffers()">Remove Offer</button>

          </td>
        </tr>
        <tr>
          <td colspan="2" class="text-end fw-bold">Base Price</td>
          <td class="bg-light fw-bold">${{ basePrice }}</td>
        </tr>
        <tr>
          <td colspan="2" class="text-end fw-bold ">Total Discount</td>
          <td class="bg-light fw-bold">${{ totalDiscount }}</td>
        </tr>
        <tr>
          <td colspan="2" class="text-end fw-bold bg-light">Total</td>
          <td class="bg-light fw-bold">${{ total }}</td>
        </tr>
      </tbody>
    </table>
    <div class="p-3 bg-light border text-center" *ngIf="carts.length == 0">
      <span>
        Empty Cart
      </span>
    </div>
  </div>
  
</div>
